mapper.select:
select *
from (select *
 from (select A.*, rownum rn
    from (

            SELECT 
            FROM xxl_job_log t


                    AND t.job_group = #{jobGroup}


                    AND t.job_id = #{jobId}


                    AND t.trigger_time  >=  #{triggerTimeStart}


                    AND t.trigger_time  <=  #{triggerTimeEnd}


                    AND t.handle_code = 200


                    AND (
                        t.trigger_code NOT IN (0, 200) OR
                        t.handle_code NOT IN (0, 200)
                    )


                    AND t.trigger_code = 200
                    AND t.handle_code = 0


            ORDER BY t.trigger_time DESC) A)
where rn > #{offset}) B
	where rn < #{offset}+ #{pagesize}

SELECT count(1)
FROM xxl_job_log t


		AND t.job_group = #{jobGroup}


		AND t.job_id = #{jobId}


		AND t.trigger_time  >=  #{triggerTimeStart}


		AND t.trigger_time  <=  #{triggerTimeEnd}


		AND t.handle_code = 200


		AND (
			t.trigger_code NOT IN (0, 200) OR
			t.handle_code NOT IN (0, 200)
		)


		AND t.trigger_code = 200
		AND t.handle_code = 0

SELECT 
FROM xxl_job_log t
WHERE t.id = #{id}

mapper.update:
UPDATE xxl_job_log
SET 
	handle_time= #{handleTime,jdbcType=DATE},
	handle_code= #{handleCode,jdbcType=VARCHAR},
	handle_msg= #{handleMsg,jdbcType=CLOB}
WHERE id= #{id}

mapper.select:
select id from
(select id,rownum rn from (
SELECT id FROM xxl_job_log


		AND job_group = #{jobGroup}


		AND job_id = #{jobId}


		AND trigger_time  <=  #{clearBeforeTime}


		AND id NOT in(
		SELECT id FROM(
			SELECT id FROM xxl_job_log t


					AND t.job_group = #{jobGroup}


					AND t.job_id = #{jobId}


			ORDER BY t.trigger_time desc

			) t1 where where   rownum < #{clearBeforeNum} 
		)


order by id asc
) )
where   rn < #{pagesize}

mapper.delete:
delete from xxl_job_log
WHERE id in

	#{item}

mapper.update:
UPDATE xxl_job_log
SET
	alarm_status = #{newAlarmStatus}
WHERE id= #{logId} AND alarm_status = #{oldAlarmStatus}
